mapper.insert:
INSERT
  INTO
  		DRAW_BOARD
  		(
  		DRAW_NO
  		,TITLE
  		,USER_NO
  		,BOOK_WRITER
  		,GENRE
  		,CLOSE_DATE
  		,TRY_POINT
  		,CONTENT
  		,ORIGIN_NAME
  		,CHANGE_NAME
  		)
VALUES
		(
		SEQ_DNO.NEXTVAL
		,#{title}
		,#{userNo}
		,#{bookWriter}
		,#{genre}
		,#{closeDate}
		,#{tryPoint}
		,#{content}
		,#{originName}
		,#{changeName}
		)

INSERT
  INTO
  		AUCTION_BOARD
  		(
  		AUCTION_NO
  		,TITLE
  		,USER_NO
  		,BOOK_WRITER
  		,GENRE
  		,CLOSE_DATE
  		,INSTANTLY_PRICE
  		,START_PRICE
  		,BID_PRICE
  		,NOW_PRICE
  		,CONTENT
  		,ORIGIN_NAME
  		,CHANGE_NAME
  		)
VALUES
		(
		SEQ_ANO.NEXTVAL
		,#{title}
		,#{userNo}
		,#{bookWriter}
		,#{genre}
		,#{closeDate}
		,#{instantlyPrice}
		,#{startPrice}
		,#{bidPrice}
		,#{startPrice}
		,#{content}
		,#{originName}
		,#{changeName}
		)

mapper.update:
UPDATE
		DRAW_BOARD
   SET
   		PRIZE_USER = #{userNo}
 WHERE
 		DRAW_NO = #{boardNo}

UPDATE
		AUCTION_BOARD
   SET
   		PRIZE_USER = #{userNo}
   		,NOW_PRICE = #{bidPrice}
 WHERE
 		AUCTION_NO = #{boardNo}
