mapper.insert:
insert
  into
  	   used_board
       (
       used_no
      ,user_no
      ,used_title
      ,book_author
      ,book_genre
      ,point
      ,book_title
      ,used_content
      ,origin_name
      ,change_name
      ,stock_status
       )
values
	   (
	   seq_useno.nextval
	  ,#{userNo}
	  ,#{usedTitle}
	  ,#{bookAuthor}
	  ,#{bookGenre}
	  ,#{point}
	  ,#{bookTitle}
	  ,#{usedContent}
	  ,#{originName}
	  ,#{changeName}
	  ,#{stockStatus} 
	   )

mapper.update:
update
       used_Board
   set
        used_title = #{usedTitle}
       ,book_author = #{bookAuthor}
       ,book_genre = #{bookGenre}
       ,point = #{point}
       ,change_name = #{changeName}
       ,origin_name = #{originName}
       ,book_title = #{bookTitle}
       ,used_content = #{usedContent}
 where
       used_no = #{usedNo}
